def read_from_json(file_path):
        with open(file_path, 'r') as fp:
            return json.load(fp)

def write_json_to_text_file(file_path, json_data):
    if json_data:
        with open(file_path, 'w') as fp:
            json.dump(json_data, fp)
            fp.close()
    


